import React from 'react'
import Note from './Note'

class Board extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            notes: [
                {
                    id:33,
                    note :"Call Lisa"
                },
                {
                    id:34,
                    note: "Email John"
                }          
            ]
        }
    }
    eachNote(note, i) {
        return (
            <Note key ={i}
                index = {i}>
                {note.note}
                </Note>
        )
    }
    render() {
        return (
            <div className="board">
                {this.state.notes.map(this.eachNote)}
            </div>
        )
    }
}

export default Board
